mapper.select:
SELECT COUNT(*)
FROM usereyeinfo
WHERE user_id = #{userId}

SELECT ROUND(total_blink_times / (total_blink_times + warning_count), 2)
FROM usereyeinfo
WHERE user_id = #{userId}

SELECT ROUND(warning_count / (total_blink_times + warning_count), 2)
FROM usereyeinfo
WHERE user_id = #{userId}
